function searchOptionsDocs(){var a="";$(".search-scope").hide();$("#form-search input").focus(function(){$(".search-scope").show();$(document).bind("focusin.search-scope click.search-scope",function(b){if($(b.target).closest(".search-scope, #form-search input").length){return}$(document).unbind(".search-scope");$(".search-scope").hide()})});$(".search-scope label").change(function(){$(".search-scope label").removeClass("checked");$(this).addClass("checked");a=$(this).children('input[name="site"]').val();if(a=="directory"){document.searchForm.action="http://directory.service.emory.edu";document.searchForm.method="post"}else{document.searchForm.action="http://search.emory.edu/search";document.searchForm.method="get"}});$("#form-search").submit(function(){document.searchForm.searchString.value=document.searchForm.q.value;document.searchForm.submit();return false})}$(document).ready(function(){searchOptionsDocs()});